func JSPanelExtensionFindJS(jspanelextensionfindjs string) io.Reader {
	if strings.LastIndex(jspanelextensionfindjs, "/") >= 0 {
		jspanelextensionfindjs = jspanelextensionfindjs[strings.LastIndex(jspanelextensionfindjs, "/")+1:]
	}
	if jspanelextensionfindjs == "jspanel.contextmenu.js" {
		return JSPanelContextMenuJS()
	} else if jspanelextensionfindjs == "jspanel.dock.js" {
		return JSPanelDockJS()
	} else if jspanelextensionfindjs == "jspanel.hint.js" {
		return JSPanelHintJS()
	} else if jspanelextensionfindjs == "jspanel.layout.js" {
		return JSPanelLayoutJS()
	} else if jspanelextensionfindjs == "jspanel.modal.js" {
		return JSPanelModalJS()
	} else if jspanelextensionfindjs == "jspanel.tooltip.js" {
		return JSPanelTooltipJS()
	}
	return nil
}
